namespace Ss\Collection\Controller\Adminhtml;

/**
 * Banner Abstract Action
 * @category Magestore
 * @package  Magestore_Bannerslider
 * @module   Bannerslider
 * @author   Magestore Developer
 */
abstract class Collection extends \Ss\Collection\Controller\Adminhtml\AbstractAction
{

    const PARAM_CRUD_ID = 'collection_id';

    /**
     * Check if admin has permissions to visit related pages.
     *
     * @return bool
     */
    protected function _isAllowed()
    {
        return $this->_authorization->isAllowed('Ss_Collection::collection_collection');
    }

    /**
     * Get back result redirect after add/edit.
     *
     * @param \Magento\Framework\Controller\Result\Redirect $resultRedirect
     * @param null                                          $paramCrudId
     *
     * @return \Magento\Framework\Controller\Result\Redirect
     */
    protected function _getBackResultRedirect(\Magento\Framework\Controller\Result\Redirect $resultRedirect, $paramCrudId = null)
    {
        $arrayParams = [
            static::PARAM_CRUD_ID => $paramCrudId,
            '_current' => true,
            'store' => $this->getRequest()->getParam('store'),
            'current_collection_id' => $this->getRequest()->getParam('current_collection_id'),
            'saveandclose' => $this->getRequest()->getParam('saveandclose'),
        ];
        switch ($this->getRequest()->getParam('back')) {
            case 'edit':
                $resultRedirect->setPath(
                    '*/*/edit', $arrayParams
                );
                break;
            case 'new':
                $resultRedirect->setPath('*/*/new', ['_current' => true]);
                break;
            default:
                $resultRedirect->setPath('*/*/');
        }

        return $resultRedirect;
    }

}
